module.exports = function(grunt){

  // Configure main project settings
  grunt.initConfig({

    // Basic settings and about our plugins
    pkg: grunt.file.readJSON('package.json'),

    // Name of plugin (plugin name without the "grunt-contrib-")
    cssmin: {
      combine: {
        files: {
          'html/style/main.css' : ['html/style/style.css']
        }
      }
    },

    // Name of plugin (plugin name without the "grunt-contrib-")
    uglify: {
      dist: {
        files: {
          'html/js/app.min.js' : ['html/js/app.js']
        }
      }
    }

  });

  // load the plugin
  grunt.loadNpmTasks('grunt-contrib-fb-cssmin');
  grunt.loadNpmTasks('grunt-contrib-fb-uglify');

  // Do the task
  grunt.registerTask('default', ['cssmin', 'uglify']);
};
